INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Baldur Ólafsson', 4110546, 'baldur@Ca.is', 'Löggiltur fasteignasali', 'Ég er Hafnfirðingur í húð og hár og bý þar enn.  Ég hef starfað á fasteignasölu síðan haustið 2011 en er menntaður rafeindavirkja meistari.  Áður hef ég starfað við hin ýmsu sölustörf lengst af við sölu á tæknivörum og lausnum. Ég er giftur og á fjórar dætur.  Mín helstu áhugamál eru golf og enski boltinn.');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Theodór Ingi Gíslason', 4118758, 'theodor@Ca.is', 'Löggiltur fasteignasali', 'Á dauða mínum átti ég von...en svona er lífið nú skemmtilegt. Ég datt niður á þetta starf og þessa menntun eftir að hafa verið að þreyfa fyrir mér í þónokkurn tíma og mikil ósköp sem mér þykir þetta skemmtilegt starf. Ég hef starfað sem söngkona síðan á síðustu öld, rekið verslun á Laugavegi, á Söngskóla sem er 10 ára um þessar mundir, unnið í fiski, bakaríi, á elliheimili og leikskóla og allt hefur þetta mótað mig og undirbúið undir þessa nýju og spennandi braut sem ég er komin á í lífinu.');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Sindri Magnússon', 4111099, 'sindri@Ca.is', 'Löggiltur fasteignasali', 'Ég hóf störf í haust hjá Fasteignasölu Reykjavíkur og er nýr í faginu og stefni á nám til löggildingar á næstu önn.  Bakgrunnur minn er fjölbreyttur en lengst af í mínum starfsferli hef ég starfað við fjölmiðla, bæði við dagskrárgerð og framleiðslu.  Ég hef einnig um 10 ára reynslu í markaðs- og sölumálum þar sem ég hef notið mín afskaplega vel í góðum samskiptum við mína viðskiptavini.  Ég stundaði nám í Vancouver Film School og útskrifaðist þaðan með 97/100 í einkunn. Ég bý í Árbænum ásamt eiginkonu minni og þremur börnum og kunnum við afskaplega vel við okkur í því hverfi þar sem það er stutt í náttúruna og eina bestu sundlaug borgarinnar.  Ég er mikið í veiði og hef meðal annars séð um staðarhald og leiðsögn veiðimanna við Langá á Mýrum síðustu tvö sumur. Það er mitt markmið að veita þér mína bestu þjónustu og vera þér innan handar með það sem þú þarft til að viðskipti þín með Fasteignasölu Reykjavíkur verði þér og þínum ánægjuleg.');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Kári Þorkelsson', 4116710, 'kari@Ca.is', 'Löggiltur fasteignasali', 'Ég hef starfað við ýmis sölustörf í rúmlega 20 ár. Sem sölufulltrúi á fasteignasölu hef ég starfað frá 2011. Þeir sem þekkja mig vita að ég legg mig allan fram í því sem ég tek mér fyrir hendur og veiti vandaða og góða þjónustu. Ég er giftur og á þrjá syni og tvær dætur.');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Katrín Þóra Gunnarsdóttir', 4114681, 'katrin@Ca.is', 'Löggiltur fasteignasali', 'Ég hef starfað við sölu fasteigna frá árinu 2013. Minn bakgrunnur er yfir 15 ára reynsla ráðgjöf í birtingar- og markaðsmálum hérlendis og erlendis. Ég legg mig ávallt alla fram í því sem ég tek mér fyrir hendur og sinni starfi mínu af alúð, fagmennsku og jákvæðni.');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Hafdís Guðrún Þorkelsdóttir', 4117531, 'hafdis@Ca.is', 'Löggiltur fasteignasali', 'Ég hef starfað sem sölufulltrúi á fasteignasölu frá ágúst 2011. Fyrir þann tíma starfaði ég við ráðgjöf hjá SÁÁ en ég er með gráðu í áfengis-og vímuefnaráðgjöf frá Landlæknisembættinu og einnig frá NAADAC í Bandaríkjunum. Ég stundaði nám við Oklahoma State University í 3 ár í sálfræði. Ég er í sambúð, á 3 börn, 3 stjúpbörn og tvo hunda. Er áhugamanneskja um körfubolta og langar að stunda aðeins meira golf. Ég er Árbæingur í grunninn en innfluttur Grafarvogsbúi og líkar það vel. Hef lokið námi til löggildingar fasteignasala');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Aðalbjörg Helgadóttir', 4115964, 'adalbjorg@Ca.is', 'Löggiltur fasteignasali', 'Ég bý í Grafarvogi með eiginmanni og tveimur börnum og tek virkan þátt í nærsamfélaginu. Áhugamál mín eru gæðastundir með fjölskyldu og vinum, ferðalög hverskonar og körfubolti.');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Sandra Marín Kristínardóttir', 4115876, 'sandra@Ca.is', 'Löggiltur fasteignasali', 'Ég hef starfað á og við fasteignasölu frá byrjun árs 2007 og öðlaðist svo löggildingu vorið 2013. Áður starfaði ég við hin ýmsu verslunarstörf lengst af hjá Hagkaupum. Ég á einn son og tvo stjúpsyni. Ég bý í Reykjavík og hef áhuga á allri útiveru allan ársins hring.');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Magnhildur Magnúsdóttir', 4111025, 'magnhildur@Ca.is', 'Löggiltur fasteignasali', 'Ég hef starfað á fasteignasölu síðan sumarið 2013 en lauk prófi til löggildingar árið 2009.  Ég er Vestmannaeyingur en bý í Reykjavík með börnunum mínum þremur. Ég er söngkona af lífi og sál, syng í kórum og við hvert tækifæri sem gefst.');
INSERT INTO starfsmenn_starfsmenn (nafn, simi, netfang, starfsheiti, lysing) VALUES ('Diljá Eir Ólafsdóttir', 4117610, 'dilja@Ca.is', 'Löggiltur fasteignasali', 'Ég hef búið og starfað í Danmörku og Chile, var í Háskólanum á Akureyri í Viðskiptafræði, tók söng- og kennaranám í Kaupmannahöfn og er nú í námi til Löggildingar Fasteigna-og skipasala hjá Endurmenntun Háskóla Íslands.');
