INSERT INTO eignir_Eign (heimilisfang, baejarfelag, postnumer, verd, "brunabotamat", "fasteignamat", tegund, lysing, staerd, byggingarar, fjoldi_herbergja, fjoldi_badherberga, fjoldi_svefnherberga, sett_a_solu, laust_nuna, teikning, serinngangur, sameigninlegur_inngangur, solpallur, svalir, gardur, thvottahus, hjolastolaadgengi, lyfta, bilskur, bilastaedi, nafn_seljanda, simi_seljanda, netfang_seljanda, "starfsmenn_id") VALUES ('BORGARHÓLSSTEKKUR 6', 'SELFOSS', 800, 43000000, 22980000, 26380000, 'Sumarhús', 'Rúmgott og mikið endurnýjað 80 fm sumarhús ásamt um 20 fm gestahúsi samtals um 100 fm. við Borgarhólsstekk á Þingvöllum. Húsið stendur á 8.642 fm eignarlandi og með húsinu fylgir einkabryggja við vatnið. Húsið er í afgirtu hverfi með rafmagnshliði.
