INSERT INTO eignir_Eign (heimilisfang, baejarfelag, postnumer, verd, "brunabotamat", "fasteignamat", tegund, lysing, staerd, byggingarar, fjoldi_herbergja, fjoldi_badherberga, fjoldi_svefnherberga, sett_a_solu, laust_nuna, teikning, serinngangur, sameigninlegur_inngangur, solpallur, svalir, gardur, thvottahus, hjolastolaadgengi, lyfta, bilskur, bilastaedi, nafn_seljanda, simi_seljanda, netfang_seljanda, "starfsmenn_id") VALUES ('LAUGARNESVEGUR 110', 'REYKJAVÍK', 105, 32900000, 16600000, 27350000, 'Fjölbýli', 'Mjög góð 2ja herbergja 59,5 fm íbúð  með sérinngang á jarðhæð. Húsið hefur mikið verið endurnýjað síðustu ár. Nýverið var skólp endurnýjað í blokkinni. Einnig voru allar ofnalagnir endurnýjaðar og ofnar yfirfarnir. Þá er ný útidyrahurð, ný rafmagnstafla og nýtt dyrasímakerfi. Húsið var sprunguviðgert og málað fyrir nokkrum árum.  Húsið er  steinsteypt og byggt árið 1956.
INSERT INTO eignir_Eign (heimilisfang, baejarfelag, postnumer, verd, "brunabotamat", "fasteignamat", tegund, lysing, staerd, byggingarar, fjoldi_herbergja, fjoldi_badherberga, fjoldi_svefnherberga, sett_a_solu, laust_nuna, teikning, serinngangur, sameigninlegur_inngangur, solpallur, svalir, gardur, thvottahus, hjolastolaadgengi, lyfta, bilskur, bilastaedi, nafn_seljanda, simi_seljanda, netfang_seljanda, "starfsmenn_id") VALUES ('BAUGHÚS 20', 'REYKJAVÍK', 112, 69900000, 45250000, 55500000, 'Hæð', 'Baughús 20,  161.6 fm 4 herb. íbúð með bílskúr, heitum potti og miklu útsýni.  
INSERT INTO eignir_Eign (heimilisfang, baejarfelag, postnumer, verd, "brunabotamat", "fasteignamat", tegund, lysing, staerd, byggingarar, fjoldi_herbergja, fjoldi_badherberga, fjoldi_svefnherberga, sett_a_solu, laust_nuna, teikning, serinngangur, sameigninlegur_inngangur, solpallur, svalir, gardur, thvottahus, hjolastolaadgengi, lyfta, bilskur, bilastaedi, nafn_seljanda, simi_seljanda, netfang_seljanda, "starfsmenn_id") VALUES ('SKEIÐARVOGUR 29', 'REYKJAVÍK', 104, 67000000, 42980000, 56950000, 'Raðhús', 'Gengið er inn um sérinngang og þaðan er komið inn í flísalagða forstofu, sem er með fatahengi og fataskáp. Úr forstofunni er gengið inn á gestasnyrtingu, sem er með flísum á gólfi og á veggjum að hluta. Eldhúsið er með I-laga innréttingu beggja vegna gangs, flísar á milli skápa. Eldhúskrókur. Búr innaf eldhúsi. Parket á gólfi. Herbergið á hæðinni er parketlagt. Stofan er parketlögð, hún er afar björt og úr henni er gengið út á svalir og af þeim niður í garð. Gengið er upp tréstiga á efri hæð íbúðarinnar. Á efri hæðinni eru þrjú svefnherbergi, öll parketlögð og hjónaherbergið með góðum fataskápum. Gengið er út á svalir úr hjónaherbergi. Baðherbergið er með flísalögðu gólfi og veggjum að hluta, innrétting og skápar, baðkar með sturtuskýli og stæði og tengi fyrir þvottavél og þurrkara.
INSERT INTO eignir_Eign (heimilisfang, baejarfelag, postnumer, verd, "brunabotamat", "fasteignamat", tegund, lysing, staerd, byggingarar, fjoldi_herbergja, fjoldi_badherberga, fjoldi_svefnherberga, sett_a_solu, laust_nuna, teikning, serinngangur, sameigninlegur_inngangur, solpallur, svalir, gardur, thvottahus, hjolastolaadgengi, lyfta, bilskur, bilastaedi, nafn_seljanda, simi_seljanda, netfang_seljanda, "starfsmenn_id") VALUES ('ANDRÉSBRUNNUR 17', 'REYKJAVÍK', 113, 52500000, 40880000, 43900000, 'Fjölbýli', 'Gengið er inn í parketlagða forstofu sem er með fataskáp. Við forstofuna er annars vegar gluggalaust þvottahús, sem er með flísalögðu gólfi og stæði og tengi fyrir þvottavél og þurrkara. Hins vegar er gengið inn í skráða geymslu, sem auðveldlega má nýta sem svefnherbergi en herbergið er parketlagt og með gluggum. Þrjú önnur svefnherbergi eru í íbúðinni, öll eru þau parketlögð og með fataskápum. Baðherbergið er flísalagt í hólf og gólf, það er með innréttingu og hillum og baðkari með sturtuskýli. Rýmið er gluggalaust. Stofan og eldhúsið eru í opnu rými. Stofan er björt og úr henni er gengið út á góðar suðursvalir. Eldhúsið er með L-laga innréttingu, neðri og efri skápum. Parket á gólfi í alrýminu.
INSERT INTO eignir_Eign (heimilisfang, baejarfelag, postnumer, verd, "brunabotamat", "fasteignamat", tegund, lysing, staerd, byggingarar, fjoldi_herbergja, fjoldi_badherberga, fjoldi_svefnherberga, sett_a_solu, laust_nuna, teikning, serinngangur, sameigninlegur_inngangur, solpallur, svalir, gardur, thvottahus, hjolastolaadgengi, lyfta, bilskur, bilastaedi, nafn_seljanda, simi_seljanda, netfang_seljanda, "starfsmenn_id") VALUES ('BRÆÐRABORGARSTÍGUR 55', 'REYKJAVÍK', 101, 29900000, 14800000, 29050000, 'Fjölbýli', 'Vel skipulögð 52 fm íbúð á 2. hæð í þessu fallega og mikið endurnýjaða fjölbýlishúsi. Húsið er hannað af Guðjóni Samúelssyni, húsameistara ríkisins. 
INSERT INTO eignir_Eign (heimilisfang, baejarfelag, postnumer, verd, "brunabotamat", "fasteignamat", tegund, lysing, staerd, byggingarar, fjoldi_herbergja, fjoldi_badherberga, fjoldi_svefnherberga, sett_a_solu, laust_nuna, teikning, serinngangur, sameigninlegur_inngangur, solpallur, svalir, gardur, thvottahus, hjolastolaadgengi, lyfta, bilskur, bilastaedi, nafn_seljanda, simi_seljanda, netfang_seljanda, "starfsmenn_id") VALUES ('ÓÐINSGATA 4', 'REYKJAVÍK', 101, 44500000, 21820000, 40700000, 'Fjölbýli', 'Gengið er inn í parketlagðan forstofugang. Á hægri hönd eru tvö samliggjandi svefnherbergi (annað þeirra stofa á grunnteikningu) og eru þau bæði parketlögð. Í öðru herberginu er fataskápur. Einstakt útsýni úr svefnherbergjum til vesturs, þ.a.m. yfir Snæfellsjökul. Baðherbergið er flísalagt í hólf og gólf, skápur, baðkar með sturtuskýli, stæði og tengi fyrir þvottavél. Rýmið er gluggalaust. Eldhúsið er með L-laga innréttingu, neðri skrápum og efri að hluta, parket á gólfi. Borðaðstaða í eldhúsi. Stofan er parketlögð og úr henni er fallegt útsýni yfir miðbæinn og Hallgrímskirkju.
INSERT INTO eignir_Eign (heimilisfang, baejarfelag, postnumer, verd, "brunabotamat", "fasteignamat", tegund, lysing, staerd, byggingarar, fjoldi_herbergja, fjoldi_badherberga, fjoldi_svefnherberga, sett_a_solu, laust_nuna, teikning, serinngangur, sameigninlegur_inngangur, solpallur, svalir, gardur, thvottahus, hjolastolaadgengi, lyfta, bilskur, bilastaedi, nafn_seljanda, simi_seljanda, netfang_seljanda, "starfsmenn_id") VALUES ('HJARÐARHAGI 21', 'REYKJAVÍK', 107, 61900000, 35450000, 58200000, 'Fjölbýli', 'Komið er inn um sameiginlegan inngang með íbúð á 2. hæð. Gengið er upp breiðan teppalagðan stiga. Komið er inn í parketlagt hol með fatahengi. Stór fallegur gluggi er í holi. Inn af holi er þvottahús. Stofurnar eru tvær, samliggjandi. Gólf er parketlagt. Mikil lofthæð er í stofum. Góðar svalir til suðurs eru út af stofum. Eldhúsið er inn af stofum og er mjög rúmgott. Gólf er parketlagt og innrétting í eldhúsi er máluð. Borðkrókur er í eldhúsi. Lögn fyrir uppþvottavél. Herbergin eru þrjú. Þau eru öll parketlögð og með skápum. Baðherbergið er flísalagt. Baðkar með sturtu í. Handklæðaofn. Gluggi er í baðherbergi. Í kjallara er herbergi/geymsla og aðgangur að sturtu og salerni.
